export default{
	data(){
		return{
			limitUpload:1,
			fileTemp:null,
			file:null,
			da:[],
			dalen:0
		};
	},
	methods:{
		// 添加文件和上传成功和失败都会执行该文件
		handleChange(file, fileList){ 
			console.log("handleChangec参数：",file);
		    this.fileTemp = file.raw; //获取上传文件的头部的信息
		    if(this.fileTemp){ //判断头部是不是为空
		        if((this.fileTemp.type == 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet') 
		            || (this.fileTemp.type == 'application/vnd.ms-excel')){ //判断头部是否符合设置
		            this.importfxx(this.fileTemp); //调用文件上传方法
		        } else { //文件格式不对时，提示信息
		            this.$message({
		                type:'warning',
		                message:'附件格式错误，请删除后重新上传！'
		            })
		        }
		    } else { //文件头部是空
		        this.$message({
		            type:'warning',
		            message:'请上传附件！'
		        })
		    }
		},
		//超出文件最大的数量，有关参数limitUpload
		handleExceed(){
		    this.$message({
		        type:'warning',
		        message:'超出最大上传文件数量的限制！'
		    })
		    return;
		},
